function AvatarContainer(props){

let veri;
let source;
let title;


if (props.name){
  veri = props.name
}

if(!props.name){
  veri = "kullanım dışı"
}


if (props.url){
  source = props.url
}
if(!props.url)
        source = "data:image/jpeg;base64,"

if (props.aciklama){
  title = props.aciklama
}else{
  title = "logo yok"
}


return <div className="avatar-container">

<div className="flex-cont">

<div className='buyer'>
  <img title={title} src={source} alt="doga" />
</div>

<h3>{veri}</h3>
<p>Lorem, ipsum dolor sit amet consectetur adipisicing elit.</p>
<p>Lorem ipsum dolor sit amet.</p>

</div>

</div>


}

